        /// <summary>
        /// Show the about dialog
        /// </summary>
        /// <param name="sender"></param>
        /// <param name="e"></param>
        private void aideToolStripMenuItem_Click(object sender, EventArgs e)
        {
            aboutbox.ShowDialog();
        }

        /// <summary>
        /// Put all the level names in a dropdown list
        /// </summary>
        private void ShowLevelName()
        {
            levels.Where(l => !l.bonus)
                .ToList()
                .Select(l => l.name)
                .ToList()
                .ForEach(name =>
                {
                    var tmp = new ToolStripMenuItem(name) {CheckOnClick = true};
                    tmp.CheckStateChanged += level_click;
                    tsmStartLevel.DropDownItems.Add((tmp));
                });

        }

        /// <summary>
        /// Handle the request of changing levels
        /// </summary>
        /// <param name="sender"></param>
        /// <param name="e"></param>
        private void level_click(object sender, EventArgs e)
        {
            ToolStripMenuItem item = (ToolStripMenuItem) sender;
            if(item.Checked)
            {
                foreach (ToolStripMenuItem menuItem in tsmStartLevel.DropDownItems)
                {
                    if (menuItem!= item)
                        menuItem.Checked = false;
                }
            }
            var level = levels.FirstOrDefault(l => l.name == item.Text);
            this.model.LoadLevel(level);
        }

        #endregion

        #region Game

        /// <summary>
        /// the main game loop
        /// </summary>
        /// <param name="sender"></param>
        /// <param name="e"></param>
        private void tmrGameLoop_Tick(object sender, EventArgs e)
        {
            this.model.Update(this.watch.ElapsedMilliseconds);
            if (this.model.Changed)
            {
                this.view.Update(this.model.GetView());
                this.model.Changed = false;
            }
        }

        #region Key
        /// <summary>
        /// Key up handler
        /// </summary>
        /// <param name="sender"></param>
        /// <param name="e"></param>
        private void BDController_KeyUp(object sender, KeyEventArgs e)
        {
            //select the key if it exist in the list
            var key = keys.FirstOrDefault(elem => elem.KeyCode == e.KeyCode);

            //If a key was found then updates its state
            if (key != null)
            {
                key.Pressed = false;
                UpdateKeyPressed();
            }
        }

        /// <summary>
        /// Update the form with the key pressed
        /// </summary>
        private void UpdateKeyPressed()
        {
            string show = "Key pressed : ";
            //Get the key pressed and gets their representation
            keys.Where(key => key.Pressed == true).ToList().ForEach(x => { show += x.ToString() + ""; });
            this.sslKeyPressed.Text = show;
        }

        /// <summary>
        /// Key down handler
        /// </summary>
        /// <param name="sender"></param>
        /// <param name="e"></param>
        private void BDController_KeyDown(object sender, KeyEventArgs e)
        {
            //select the key if it exist in the list
            var key = keys.FirstOrDefault(elem => elem.KeyCode == e.KeyCode);

            //If a key was found then updates its state
            if (key != null)
            {
                key.Pressed = true;
                UpdateKeyPressed();
            }
        }
        #endregion
